-- Дані про відгуки для бази даних "Туристичне агентство"

-- Налаштування кодування
SET client_encoding = 'UTF8';

-- Очищення таблиці перед додаванням даних
TRUNCATE TABLE відгуки CASCADE;

-- Додавання відгуків
INSERT INTO відгуки (клієнт_id, тур_id, готель_id, оцінка, коментар, дата_відгуку, модерований) VALUES
-- Відгуки про тури в Україні
((SELECT id FROM клієнти WHERE прізвище = 'Петренко' AND імя = 'Іван'), (SELECT id FROM тури WHERE назва = 'Культурний Київ'), NULL, 5, 'Чудовий тур! Київ - дивовижне місто з багатою культурою та історією. Екскурсії були дуже інформативними, а готель Premier Palace просто розкішний. Дуже задоволений обслуговуванням та організацією туру.', '2023-05-27', true),
-- Клієнт 2, Тур 5 (SPA Карпати) - цей тур закоментований в data_tours.sql, тому цей відгук пропускаємо
-- ((SELECT id FROM клієнти WHERE прізвище = 'Сидоренко' AND імя = 'Марія'), (SELECT id FROM тури WHERE назва = 'SPA-відпочинок в Карпатах'), NULL, 4, 'Дуже приємний відпочинок у Карпатах. SPA-процедури були чудовими, персонал дуже уважний. Єдине, що трохи розчарувало - погана погода два дні з чотирьох, але це, звісно, не вина туристичного агентства.', '2023-04-25', true),
((SELECT id FROM клієнти WHERE прізвище = 'Ковальчук' AND імя = 'Олена'), (SELECT id FROM тури WHERE назва = 'Одеський бриз'), NULL, 5, 'Незабутній відпочинок в Одесі! Готель Bristol - це справжня перлина міста, з чудовою архітектурою та атмосферою. Екскурсії старою Одесою були дуже цікавими, гід розповів багато захоплюючих історій.', '2023-07-16', true),
((SELECT id FROM клієнти WHERE прізвище = 'Мельник' AND імя = 'Петро'), (SELECT id FROM тури WHERE назва = 'Вихідні у Львові'), NULL, 4, 'Львів - це місто, яке викликає захоплення. Дуже сподобалась екскурсія та дегустація кави. Єдиний мінус - в номері був не дуже гарний вид з вікна, але все інше було прекрасно.', '2023-04-18', true),
((SELECT id FROM клієнти WHERE прізвище = 'Шевченко' AND імя = 'Тетяна'), (SELECT id FROM тури WHERE назва = 'Замки Західної України'), NULL, 5, 'Неймовірна подорож замками Західної України! Кожен замок мав свою унікальну історію та архітектуру. Організація туру на вищому рівні, комфортний транспорт, професійний гід. Дуже рекомендую!', '2023-06-13', true),

-- Відгуки про тури в Туреччину
((SELECT id FROM клієнти WHERE прізвище = 'Бондаренко' AND імя = 'Андрій'), (SELECT id FROM тури WHERE назва = 'Казкова Каппадокія'), NULL, 4, 'Каппадокія - це щось нереальне! Політ на повітряній кулі на світанку - один з найкращих досвідів у моєму житті. Печерний готель був дуже атмосферним, хоча трохи прохолодно вночі.', '2023-05-09', true),
((SELECT id FROM клієнти WHERE прізвище = 'Кравченко' AND імя = 'Наталія'), (SELECT id FROM тури WHERE назва = 'Курорти Хургади'), NULL, 3, 'Хургада загалом сподобалась, але були деякі проблеми з харчуванням в готелі. Море та пляж чудові, персонал доброзичливий. Хотілося б більше різноманітності в меню.', '2023-11-20', true),
((SELECT id FROM клієнти WHERE прізвище = 'Олійник' AND імя = 'Сергій'), (SELECT id FROM тури WHERE назва = 'Історичний Стамбул'), NULL, 5, 'Стамбул - це місто контрастів! Блакитна мечеть, Айя-Софія, палац Топкапи - все вражає своєю величчю та красою. Гід був дуже знаючим та відповів на всі наші питання. Дуже задоволені туром!', '2023-09-16', true),

-- Відгуки про тури в Єгипет
((SELECT id FROM клієнти WHERE прізвище = 'Пономаренко' AND імя = 'Вікторія'), (SELECT id FROM тури WHERE назва = 'Піраміди та пляжі'), NULL, 5, 'Дайвінг у Шарм-ель-Шейху - це неймовірно! Різноманітність підводного світу просто вражає. Готель Rixos був розкішним, з хорошою системою "все включено". Обов''язково повернемось сюди ще!', '2023-11-20', true),
((SELECT id FROM клієнти WHERE прізвище = 'Іванов' AND імя = 'Юрій'), (SELECT id FROM тури WHERE назва = 'Афінські хроніки'), NULL, 4, 'Акрополь та Парфенон справили величезне враження! Дуже цікава екскурсія. Єдиний мінус - було дуже багато туристів і інколи важко було почути гіда. Але загалом дуже задоволені поїздкою.', '2023-05-21', true),

-- Відгуки про тури в інші країни
((SELECT id FROM клієнти WHERE прізвище = 'Smith' AND імя = 'John'), (SELECT id FROM тури WHERE назва = 'Одеський бриз'), NULL, 5, 'Одеса - перлина біля моря! Дуже сподобалась Одеська опера та архітектура міста. Готель Bristol має чудове розташування, а персонал дуже дружелюбний. З задоволенням повернемось!', '2023-06-15', true),
((SELECT id FROM клієнти WHERE прізвище = 'Garcia' AND імя = 'Maria'), (SELECT id FROM тури WHERE назва = 'Культурний Київ'), NULL, 4, 'Київ - чудове місто з багатою історією. Особливо вразив Києво-Печерська Лавра та Софійський собор. Англомовний гід був дуже професійним. Єдине побажання - більше вільного часу для самостійних прогулянок.', '2023-04-19', true),
((SELECT id FROM клієнти WHERE прізвище = 'Müller' AND імя = 'Hans'), (SELECT id FROM тури WHERE назва = 'Вихідні у Львові'), NULL, 5, 'Львів - це справжнє європейське місто з неповторною атмосферою! Кава, шоколад, архітектура - все на вищому рівні. Дуже сподобались рекомендовані ресторани з традиційною українською кухнею.', '2023-03-24', true),
((SELECT id FROM клієнти WHERE прізвище = 'Dubois' AND імя = 'Pierre'), (SELECT id FROM тури WHERE назва = 'Замки Західної України'), NULL, 5, 'Захоплююча подорож замками України! Кожен замок унікальний та має свою цікаву історію. Гід був дуже знаючим та ентузіастичним. Дуже задоволені організацією туру та розміщенням.', '2023-05-30', true),
-- Клієнт 25, Тур 5 (SPA Карпати) - цей тур закоментований в data_tours.sql, тому цей відгук пропускаємо
-- ((SELECT id FROM клієнти WHERE прізвище = 'Rossi' AND імя = 'Isabella'), (SELECT id FROM тури WHERE назва = 'SPA-відпочинок в Карпатах'), NULL, 4, 'SPA-відпочинок в Карпатах був саме тим, що нам було потрібно! Чудові процедури, смачна їжа, красива природа. Персонал допоміг з перекладом та був дуже уважним. Рекомендуємо!', '2023-04-07', true),

-- Відгуки зі статусом "На модерації"
((SELECT id FROM клієнти WHERE прізвище = 'Петренко' AND імя = 'Іван'), (SELECT id FROM тури WHERE назва = 'Сонячна Анталія'), NULL, 3, 'Анталія загалом сподобалась, але були проблеми з обслуговуванням в готелі. Іноді доводилося довго чекати на офіціанта, а деякі страви були не дуже смачними. Пляж та море чудові, але сервіс потребує покращення.', '2023-06-23', false),
((SELECT id FROM клієнти WHERE прізвище = 'Ковальчук' AND імя = 'Олена'), (SELECT id FROM тури WHERE назва = 'Сімейний рай на Мальдівах'), NULL, 2, 'Дуже розчарований туром до Мальдів. Готель не відповідав заявленим 5 зіркам, були проблеми з кондиціонером та гарячою водою. За такі гроші очікував набагато кращого сервісу та умов.', '2023-04-15', false),
((SELECT id FROM клієнти WHERE прізвище = 'Шевченко' AND імя = 'Тетяна'), (SELECT id FROM тури WHERE назва = 'Розкішний Дубай'), NULL, 3, 'Дубай - місто контрастів. З одного боку - розкіш та сучасність, з іншого - проблеми з таксі та завищені ціни на все. Готель був гарним, але розташований далеко від основних пам''яток.', '2023-09-18', false),

-- Відгуки зі статусом "Відхилено"
-- Клієнт 2, Тур 9 (Шарм-ель-Шейх) - цей тур закоментований в data_tours.sql, тому цей відгук пропускаємо
-- ((SELECT id FROM клієнти WHERE прізвище = 'Сидоренко' AND імя = 'Марія'), (SELECT id FROM тури WHERE назва = 'Дайвінг у Шарм-ель-Шейху'), NULL, 1, 'Жахливий тур! Готель брудний, їжа несмачна, гід постійно запізнювався. Вимагаю повернення коштів! Ніколи більше не скористаюсь послугами цього агентства!', '2023-10-10', false),
((SELECT id FROM клієнти WHERE прізвище = 'Мельник' AND імя = 'Петро'), (SELECT id FROM тури WHERE назва = 'Сімейний відпочинок в Абу-Дабі'), NULL, 1, 'Повний провал! Організація на нульовому рівні, екскурсії скасовували без попередження, готель не відповідав фотографіям на сайті. Категорично не рекомендую!', '2023-03-30', false),
((SELECT id FROM клієнти WHERE прізвище = 'Бондаренко' AND імя = 'Андрій'), (SELECT id FROM тури WHERE назва = 'Пустельне сафарі в ОАЕ'), NULL, 2, 'Дуже поганий досвід. Переліт затримали, трансфер не зустрів, в готелі не було наших бронювань. Довелося самостійно вирішувати всі проблеми. Більше з цим агентством справи не матиму.', '2023-04-15', false);

-- Оновлення послідовності ID
SELECT setval('відгуки_id_seq', (SELECT MAX(id) FROM відгуки));

-- Інформаційне повідомлення про успішне завершення імпорту
DO $$
BEGIN
    RAISE NOTICE 'Дані про відгуки успішно імпортовано. Загальна кількість записів: %', (SELECT COUNT(*) FROM відгуки);
END $$;     