-- Дані про працівників для бази даних "Туристичне агентство"

-- Налаштування кодування
SET client_encoding = 'UTF8';

-- Очищення таблиці перед додаванням даних
TRUNCATE TABLE працівники CASCADE;

-- Додавання працівників

-- Оновлення послідовності ID
SELECT setval('працівники_id_seq', (SELECT MAX(id) FROM працівники));

-- Інформаційне повідомлення про успішне завершення імпорту
DO $$
BEGIN
    RAISE NOTICE 'Дані про працівників успішно імпортовано. Загальна кількість записів: %', (SELECT COUNT(*) FROM працівники);
END $$; 