-- Дані про відгуки для бази даних "Туристичне агентство"

-- Налаштування кодування
SET client_encoding = 'UTF8';

-- Очищення таблиці перед додаванням даних
TRUNCATE TABLE відгуки CASCADE;

-- Додавання відгуків
INSERT INTO відгуки (клієнт_id, тур_id, оцінка, коментар, дата_відгуку, статус) VALUES
-- Відгуки про тури в Україні
(1, 2, 5, 'Чудовий тур! Київ - дивовижне місто з багатою культурою та історією. Екскурсії були дуже інформативними, а готель Premier Palace просто розкішний. Дуже задоволений обслуговуванням та організацією туру.', '2023-05-27', 'Опубліковано'),
(2, 5, 4, 'Дуже приємний відпочинок у Карпатах. SPA-процедури були чудовими, персонал дуже уважний. Єдине, що трохи розчарувало - погана погода два дні з чотирьох, але це, звісно, не вина туристичного агентства.', '2023-04-25', 'Опубліковано'),
(3, 3, 5, 'Незабутній відпочинок в Одесі! Готель Bristol - це справжня перлина міста, з чудовою архітектурою та атмосферою. Екскурсії старою Одесою були дуже цікавими, гід розповів багато захоплюючих історій.', '2023-07-16', 'Опубліковано'),
(4, 1, 4, 'Львів - це місто, яке викликає захоплення. Дуже сподобалась екскурсія та дегустація кави. Єдиний мінус - в номері був не дуже гарний вид з вікна, але все інше було прекрасно.', '2023-04-18', 'Опубліковано'),
(5, 4, 5, 'Неймовірна подорож замками Західної України! Кожен замок мав свою унікальну історію та архітектуру. Організація туру на вищому рівні, комфортний транспорт, професійний гід. Дуже рекомендую!', '2023-06-13', 'Опубліковано'),

-- Відгуки про тури в Туреччину
(6, 8, 4, 'Каппадокія - це щось нереальне! Політ на повітряній кулі на світанку - один з найкращих досвідів у моєму житті. Печерний готель був дуже атмосферним, хоча трохи прохолодно вночі.', '2023-05-09', 'Опубліковано'),
(7, 12, 3, 'Хургада загалом сподобалась, але були деякі проблеми з харчуванням в готелі. Море та пляж чудові, персонал доброзичливий. Хотілося б більше різноманітності в меню.', '2023-11-20', 'Опубліковано'),
(8, 7, 5, 'Стамбул - це місто контрастів! Блакитна мечеть, Айя-Софія, палац Топкапи - все вражає своєю величчю та красою. Гід був дуже знаючим та відповів на всі наші питання. Дуже задоволені туром!', '2023-09-16', 'Опубліковано'),

-- Відгуки про тури в Єгипет
(9, 10, 5, 'Дайвінг у Шарм-ель-Шейху - це неймовірно! Різноманітність підводного світу просто вражає. Готель Rixos був розкішним, з хорошою системою "все включено". Обов''язково повернемось сюди ще!', '2023-11-20', 'Опубліковано'),
(10, 15, 4, 'Акрополь та Парфенон справили величезне враження! Дуже цікава екскурсія. Єдиний мінус - було дуже багато туристів і інколи важко було почути гіда. Але загалом дуже задоволені поїздкою.', '2023-05-21', 'Опубліковано'),

-- Відгуки про тури в інші країни
(21, 3, 5, 'Одеса - перлина біля моря! Дуже сподобалась Одеська опера та архітектура міста. Готель Bristol має чудове розташування, а персонал дуже дружелюбний. З задоволенням повернемось!', '2023-06-15', 'Опубліковано'),
(22, 2, 4, 'Київ - чудове місто з багатою історією. Особливо вразив Києво-Печерська Лавра та Софійський собор. Англомовний гід був дуже професійним. Єдине побажання - більше вільного часу для самостійних прогулянок.', '2023-04-19', 'Опубліковано'),
(23, 1, 5, 'Львів - це справжнє європейське місто з неповторною атмосферою! Кава, шоколад, архітектура - все на вищому рівні. Дуже сподобались рекомендовані ресторани з традиційною українською кухнею.', '2023-03-24', 'Опубліковано'),
(24, 4, 5, 'Захоплююча подорож замками України! Кожен замок унікальний та має свою цікаву історію. Гід був дуже знаючим та ентузіастичним. Дуже задоволені організацією туру та розміщенням.', '2023-05-30', 'Опубліковано'),
(25, 5, 4, 'SPA-відпочинок в Карпатах був саме тим, що нам було потрібно! Чудові процедури, смачна їжа, красива природа. Персонал допоміг з перекладом та був дуже уважним. Рекомендуємо!', '2023-04-07', 'Опубліковано'),

-- Відгуки зі статусом "На модерації"
(1, 6, 3, 'Анталія загалом сподобалась, але були проблеми з обслуговуванням в готелі. Іноді доводилося довго чекати на офіціанта, а деякі страви були не дуже смачними. Пляж та море чудові, але сервіс потребує покращення.', '2023-06-23', 'На модерації'),
(3, 16, 2, 'Дуже розчарований туром до Мальдів. Готель не відповідав заявленим 5 зіркам, були проблеми з кондиціонером та гарячою водою. За такі гроші очікував набагато кращого сервісу та умов.', '2023-04-15', 'На модерації'),
(5, 21, 3, 'Дубай - місто контрастів. З одного боку - розкіш та сучасність, з іншого - проблеми з таксі та завищені ціни на все. Готель був гарним, але розташований далеко від основних пам''яток.', '2023-09-18', 'На модерації'),

-- Відгуки зі статусом "Відхилено"
(2, 9, 1, 'Жахливий тур! Готель брудний, їжа несмачна, гід постійно запізнювався. Вимагаю повернення коштів! Ніколи більше не скористаюсь послугами цього агентства!', '2023-10-10', 'Відхилено'),
(4, 19, 1, 'Повний провал! Організація на нульовому рівні, екскурсії скасовували без попередження, готель не відповідав фотографіям на сайті. Категорично не рекомендую!', '2023-03-30', 'Відхилено'),
(6, 20, 2, 'Дуже поганий досвід. Переліт затримали, трансфер не зустрів, в готелі не було наших бронювань. Довелося самостійно вирішувати всі проблеми. Більше з цим агентством справи не матиму.', '2023-04-15', 'Відхилено');

-- Оновлення послідовності ID
SELECT setval('відгуки_id_seq', (SELECT MAX(id) FROM відгуки));

-- Інформаційне повідомлення про успішне завершення імпорту
DO $$
BEGIN
    RAISE NOTICE 'Дані про відгуки успішно імпортовано. Загальна кількість записів: %', (SELECT COUNT(*) FROM відгуки);
END $$; 